mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT b.boa_num,
REPLACE(REPLACE(b.boa_title,'<','&lt;'),'>','&gt;') boa_title,
m.mem_nick, b.boa_date, b.boa_hit, b.boa_filename, b.boa_cate, b.boa_mode FROM board b JOIN member_detail m ON b.mem_num = m.mem_num
b.boa_cate = #{boa_cate}
ORDER BY b.boa_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE board SET
boa_uploadfile=#{boa_uploadfile}, boa_filename=#{boa_filename},
boa_title=#{boa_title}, boa_content=#{boa_content}, boa_mode=#{boa_mode} WHERE boa_num=#{boa_num}
