mapper.update:
UPDATE position SET	
	
		pos_uploadfile = #{pos_uploadfile},
		pos_filename = #{pos_filename},
	
	pos_title = #{pos_title},
	pos_content = #{pos_content} 
	WHERE pos_num = #{pos_num}

mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM (SELECT p.*, m.mem_nick FROM position_comment where pos_num=#{pos_num}) p JOIN member_detail m USING(mem_num)
ORDER BY p.poc_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
